import Header from './Header';
import React from 'react';

const layoutStyle = {
    margin: 12,
    padding: 12,
    border: '1px solid #DDD'
};

class Layout extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            loggedIn: false,
            isVolunteer: false,
            isOrganization: false
        }
    }

    render() {
        return(
            <div style={layoutStyle}>
                <Header loggedIn={this.state.loggedIn} isVolunteer={this.state.isVolunteer} isOrganization={this.state.isOrganization} />
                {/* the props.children is the content of the page */}
                {props.children}
            </div>
        )
    }
}


export default Layout;
