import React, {useState} from 'react';

class GlobalContext extends React.Component {
    componentDidMount() {
        let value = this.context;
    }
    componentDidUpdate() {
        let value = this.context;
    }
    render() {
        let value = this.context;
    }
}

GlobalContext.contextType = MyContext;


