import Header from './Header';
import React from 'react';
import GlobalProvider from './GlobalContext';

const layoutStyle = {
    margin: 12,
    padding: 12,
    border: '1px solid #DDD'
};

const Layout = (props) => (
    <div style={layoutStyle}>
        <GlobalProvider>
            <Header />
            {/* the props.children is the content of the page */}
            {props.children}
        </GlobalProvider>
    </div>
);


export default Layout;
