import Layout from '../components/TheLayout';
import Header from '../components/Header';
import React from 'react';

// need this to change the isLoggedIn state of the header
// also will change the state if isVolunteer depending on how they log in

export class Login extends React.Component {
    constructor(props){
        super(props);
        this.handleLogin = this.handleLogin.bind(this);
    }

    handleLogin(e) {
        const isLoggedIn = e.target.value;
        const isVolunteer = e.target.value;
        this.props.onLogin(isLoggedIn, isVolunteer);
    }

    render() {
        return (
            <Layout>
                <Header />
                <p>Login</p>
            </Layout>
        )
    }
}
