import React, {useState} from 'react';

const defaultState = {
    isLoggedIn: "false",
    isVolunteer: false
};

// const GlobalContext = React.createContext({...defaultState});
const GlobalStateContext = React.createContext();
const GlobalChangeStateContext = React.createContext();

function changeStateFunc(state, action) {

}

function GlobalProvider({children}) {
    const [state, changeState] = React.useReducer(changeStateFunc, state);
    return (
        <GlobalStateContext.Provider value={state}>
            <GlobalChangeStateContext.Provider value={changeState}>
                {children}
            </GlobalChangeStateContext.Provider>
        </GlobalStateContext.Provider>
    );
}

export {GlobalStateContext, GlobalProvider};
