import Layout from '../components/TheLayout';
import React, { useContext, useState, useCallback } from 'react';

import {GlobalContext} from '../components/GlobalContext';

// need this to change the isLoggedIn state of the header
// also will change the state if isVolunteer depending on how they log in
const toggleLoggedIn = (state, newState, forceUpdate, dispatch) => {
    // dispatch(state => {
    //     state.isLoggedIn = newState;
    // })
    state.isLoggedIn = newState;
    forceUpdate();
}

const toggleType = (state, newState, forceUpdate) => {
    state.isVolunteer = newState;
    forceUpdate();
}


const Login = () => {
    const state = useContext(GlobalContext);
    const { dispatch } = state;
    const forceUpdate = React.useState()[1].bind(null, {})

    return (
        <Layout>
            <p>Login</p>

            <select onChange={(e) => toggleLoggedIn(state, e.target.value, forceUpdate, dispatch)}>
                <option value={false} >Just place holding for state, select if NOT logged in</option>
                <option value={true}>Just place holding for state, select if logged in</option>
            </select>

            <p>Here{state.isLoggedIn}</p>
            <select onChange={(e) => toggleType(state, e.target.value, forceUpdate)}>
                <option value={false}>Organization</option>
                <option value={true}>Volunteer</option>
            </select> 
        </Layout>
    )  
}

export default Login;
