import React, {useContext, useReducer, createContext} from 'react';


const defaultState = {
    isLoggedIn: false,
    isVolunteer: false  
};

const GlobalContext = createContext({...defaultState});

const GlobalReducer = (state, action) => {
    switch (action.type) {
        case SET_STATES: 
            return {
                ...state,
                isLoggedIn: { ...action.AisLoggedIn },
                isVolunteer: {...action.AisVolunteer }
            }
        default:
            return state;
    }
};

export const GlobalProvider = (props) => {
    const [state, setState] = useReducer(GlobalReducer, defaultState);
    return (
        <GlobalContext.Provider value={[state, setState]}>

        {/* // <GlobalContext.Provider value="green"> */}
            {props.children}
        </GlobalContext.Provider>
    );
};

const UseGlobalState = () => {
    const [state, setState] = useContext(GlobalContext);
    const setTheState = ({ isLoggedIn, isVolunteer }) => {
        setState({
            type: SET_STATE,
            AisLoggedIn: isLoggedIn,
            AisVolunteer: isVolunteer
        });
    };

    return {
        setTheState,
        isLoggedIn: { ...state.AisLoggedIn },
        isVolunteer: {...state.AisVolunteer },
    };
};

export default UseGlobalState;
